import React from 'react';

import './Icon.scss';

function Icon(props) {
  let img;

  switch (props.icon) {
    case 'edit':
      img = (
        <img src="data:image/png;base64," />
      );
      break;
    case 'delete':
      img = (
        <img src="data:image/png;base64," />
      );
      break;
    case 'plus':
      img = (
        <img src="data:image/png;base64," />
      );
      break;
    default:
      break;
  }

  return <div className={`Icon Icon-${props.icon}`}>{img}</div>;
}

export default Icon;
